import React from "react";

import clear from "../img/weather-icons/clear.svg";
import "./DetailsWeather.css";
class DetailsWeather extends React.Component {
  state = {
    input: ""
  };

  render() {
    for(var i=0;i<8;i++){
      {this.props.details[i].dt_txt=this.props.details[i].dt_txt.substr(7,2)}
    }
    return (
      <div className="times">
            <div>
              <div className="time">{this.props.details[1].dt_txt}</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[1].main.temp}<span>&#176;</span>C</div>
            </div>
            <div>
            <div className="time">06:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[2].main.temp}<span>&#176;</span>C</div>
            </div>


            <div>
            <div className="time">09:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[3].main.temp}<span>&#176;</span>C</div>
            </div>
            <div>
            <div className="time">12:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[4].main.temp}<span>&#176;</span>C</div>
            </div>


            <div>
            <div className="time">15:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[5].main.temp}<span>&#176;</span>C</div>
            </div>
            <div>
            <div className="time">18:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[6].main.temp}<span>&#176;</span>C</div>
            </div>

            <div>
            <div className="time">21:00</div>
              <img src={require('../img/weather-icons/mostlycloudy.svg')} alt="storm icon" />
              <div className="day">{this.props.details[7].main.temp}<span>&#176;</span>C</div>

            </div>






          </div>
    );
  }
}
export default DetailsWeather;
